import React from 'react';
import ItemList from './ItemList';
import Cart from './Cart';
import axios from 'axios';

class Main extends React.Component {
    state = {
        items:[],
        cartItems:[]
    };

    componentDidMount(){
        axios.get('http://localhost:81/items')
        .then((res) => {
            this.setState({items:res.data});
        });
    }

    updateCart = newItem => {
        console.log('got here');
        this.setState({ cartItems: [...this.state.cartItems, newItem] });
    };

    render(){
       return( 
        <div>
            <ItemList items={this.state.items} cartItems={this.state.cartItems} handleItemChoice={this.updateCart} />
            <Cart onAdd={this.updateCart} cartItems={this.state.cartItems} />
        </div>
       )
    }
}

export default Main;
