import React from 'react';

class Cart extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            cartItems:this.props.cartItems
        }
    }

    updateCart = () => {
        this.props.onAdd(this.state.cartItems);
        this.setState({cartItems:this.props.cartItems});
    }

    subtotal() {
        let subtotal = 0;
        if(this.state.cartItems.length >= 1){
           let inCart = this.state.cartItems;
            subtotal = inCart.reduce((a,b) => {
                return a.price + b.price;
            });
        } 
        console.log(subtotal);
        return subtotal;
    }

    render(){
        return(
            <div>
                <h2>CART</h2>
                <ul>
                    {this.state.cartItems.map(item => 
                        <li key={item.id}>
                            {item.name} ${item.price}
                        </li>
                    )}
                    <input type="number" value="1"/>
                </ul>
                <p>Subtotal: ${this.subtotal}</p>
                <p>Total Taxes: </p>
                <p>Total: </p>
            </div>
        );
    }

}

export default Cart;
