import React from 'react';
import Axios from 'axios';

class Cart extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            cartItems:this.props.cartItems
        }
    }

    updateCart = () => {
        this.props.onAdd(this.state.cartItems);
        this.setState({cartItems:this.props.cartItems});
    }

    subtotal() {
        let subtotal = 0;
        if(this.state.cartItems.length >= 1){
           let inCart = this.state.cartItems;
            subtotal = inCart.reduce((a,b) => {
                return a.price + b.price;
            });
        }
        return subtotal;
    }

    handleQtyChange(event) {
        console.log(event.target.value);
    }

    render(){
        let cart = <h3>Your Cart is Empty</h3>;

        if(this.props.cartItems.length >=1){
            cart = <div>
            <h2>CART</h2>
            <ul>
                {this.props.cartItems.map(item => 
                    <li key={item.id}>
                        <label>{item.name} ${item.price}</label>
                        <input key={item.id} type="number" value="1" onChange={this.handleQtyChange}
                        className="form-control"/>
                    </li>
                )}

            </ul>
            <p>Subtotal: ${this.subtotal}</p>
            <p>Total Taxes: </p>
            <p>Total: </p>
        </div>
        }

        return(
            cart
        );
    }

}

export default Cart;
