import React from 'react';

class Cart extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            cartItems:this.props.cartItems
        }
    }

    updateCart = () => {
        this.props.onAdd(this.state.cartItems);
        this.setState({cartItems:this.props.cartItems});
    }

    subtotal(){
        let subtotal = 0;
        let prices = [];
        if(this.state.cartItems.length >= 1){
            for(let i=0;i<=this.state.cartItems.length; i++){
                prices.push(this.state.cartItems[i].price);
            }

            console.log(prices);
            // subtotal = this.state.cartItems.reduce((a,b) => {
            //     return a.price + b.price;
            // });
        } 
        return subtotal;
    }

    render(){
        return(
            <div>
                <h2>CART</h2>
                <ul>
                    {this.state.cartItems.map(item => 
                        <li key={item.id}>
                            {item.name} ${item.price}
                        </li>
                    )}
                </ul>
                <p>Subtotal: {this.subtotal()}</p>
                <p>Total Taxes: </p>
                <p>Total: </p>
            </div>
        );
    }

}

export default Cart;
