import axios from 'axios';

class Cart extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            cartItems:this.props.cartItems
        }
    }

    updateState(){
        this.setState({cartItems:this.props.cartItems});
    }

    updateCart = () => {
        this.props.onAdd(this.state.cartItems);
        this.setState({cartItems:this.props.cartItems});
    }

    render(){
        return(
            <div>
                <h2>CART</h2>
                <ul>
                    {this.props.cartItems.map(item => 
                        <li>
                            {item.name} ${item.price}
                        </li>
                    )}
                </ul>
                <p>Subtotal: </p>
                <p>Total Taxes: </p>
                <p>Total: </p>
            </div>
        );
    }

}

export default Cart;
