import React from 'react';

class Cart extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            cartItems:this.props.cartItems
        }
    }

    updateCart = () => {
        this.props.onAdd(this.state.cartItems);
        this.setState({cartItems:this.props.cartItems});
    }

    subtotal(){
        const prices = this.state.cartItems.map(item => (item.price));
        return this.state.cartItems.reduce((a,b) => {
            return a + b;
        })
    }

    render(){
        return(
            <div>
                <h2>CART</h2>
                <ul>
                    {this.state.cartItems.map(item => 
                        <li>
                            {item.name} ${item.price}
                        </li>
                    )}
                </ul>
                <p>Subtotal: {this.subtotal()}</p>
                <p>Total Taxes: </p>
                <p>Total: </p>
            </div>
        );
    }

}

export default Cart;
