import React from 'react';
import ItemList from './ItemList';
import Cart from './Cart';
import axios from 'axios';

class Main extends React.Component {
    constructor(props){
        super(props);
        state = {
            items:[],
            cartItems:[]
          }
    }

    componentDidMount(){
        this.state.items = axios.get('http://localhost:81/items')
        .then(res => {
            const items = res.data;
            this.setState({items});
        });
        console.log(this.state);
    }

    updateCart = addItem => {
        this.setState({ cartItems: [...this.state.cartItems, addItem] });
      };

    render(){
       return( <div>
            <ItemList item={this.state.items}></ItemList>
            <Cart onAdd={this.updateCart}></Cart>
        </div>
       )
    }
}

export default Main;
