    updateCart = addItem => {
        this.setState({ cartItems: [...this.state.cartItems, newItem] });
      };

    render(){
       return( <div>
            <ItemList item={this.state.items}></ItemList>
            <Cart onAdd={this.updateCart}></Cart>
        </div>
       )
    }
}

export default Main;
