import React from 'react';
import logo from './logo.svg';
import './App.css';
import ItemList from './Components/ItemList';
import Cart from './Components/Cart';
import axios from 'axios';

function App() {
  state = {
    items:[],
    cartItems:[]
  };

    this.state.items = 
    axios.get('http://localhost:81/items')
        .then(res => {
            const items = res.data;
            this.setState({items});
        });

  return (
    <div className="App">
      <header className="App-header1">
        <h2>Shopping Cart</h2>
      </header>
      <div id="itemList">
        <ItemList item={this.state.items}></ItemList>
      </div>

      <div id="cart">
        <Cart handleItemChoice={this.handleItemChoice.bind(this)}></Cart>
      </div>
    </div>
  );
}

export default App;
