    }

    handleChange() {
        this.setState({cartItems:cartItems});
    }

    render(){
        return(
            <div>
                <h2>CART</h2>
                <ul>
                    {this.state.cartItems.map(item => 
                        <li key={item.id}>
                            {item.name} ${item.price}
                        </li>
                    )}
